import * as React from 'react';
import { Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { DefaultTheme, DarkTheme } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { useEffect, useState } from 'react';
import * as ml5 from "ml5";
import { getCompletion } from "gpt3";
import { Stocks } from "./Stocks";
function HomeScreen() {
  sentiment = ml5.sentiment('movieReviews', modelReady);
  var sL = false;
  var cL = false;
  var sentiment;
  var charRNN;
  var report;
  var loaded;


  useEffect(() => {

  }, [])

  function modelReady() {
    loaded = true;
    console.log("dwa")

  }
  function waitFor(conditionFunction) {

    const poll = resolve => {
      if(conditionFunction()) resolve();
      else setTimeout(_ => poll(resolve), 400);
    }

    return new Promise(poll);
  }
  async function waitUntilTrue(flag) {
    await waitFor(_ => flag === true);
    console.log('the wait is over!');
    var prediction;

    prediction = sentiment.predict(report);
    console.log(prediction);
    if (prediction.score > .5) {
      thought = "Buy!";
    }
    else {
      thought = "Sell!";
    }
    return thought;
  }
  async function Predict(name) {
    var res;
    var report;
    var thought;
    console.log("predicting");
    fetch('http://localhost:3000/api/generate?qMake%20a%20prediction%20for%20' + name + '%20stock', { mode: 'cors' }).then((response) => { res = response.text(); res.then((result) => { report = result; console.log(report)}) });
    console.log('http://localhost:3000/api/generate?qMake%20a%20prediction%20for%20' + name + '%20stock');
    waitUntilTrue(loaded);
    var pred =  [thought, report];
    console.log(report);
    return pred;
  }
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Stocks symbols={["AAPL", "TSLA", "GME"]} names={["Apple", "Tesla", "Gamestop"]} makePrediction={Predict} />
    </View>
  )
}

function SettingsScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Settings!</Text>
    </View>
  );
}

const Tab = createBottomTabNavigator();

function MyTabs() {
  return (
    <Tab.Navigator>
      <Tab.Screen name="Home" component={HomeScreen} />
      <Tab.Screen name="Settings" component={SettingsScreen} />
    </Tab.Navigator>
  );
}
const myTheme = DarkTheme;
myTheme.colors.text = 'rgb(256, 256, 256)';
myTheme.colors.background = DarkTheme.colors.border;
myTheme.colors.primary = DarkTheme.colors.primary;
export default function App() {
  return (
    <NavigationContainer theme={DarkTheme}>
      <MyTabs />
    </NavigationContainer>
  );
}
