import React from 'react';
import {
    StyleSheet,
    Text,
    View,
    TouchableOpacity,
    TouchableHighlight,
    Image,
    FlatList,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useEffect, useState } from 'react';
import { Cache } from "react-native-cache";

export default function FavoritesContent() {
    const [Stocks, setStocks] = useState([]);
    useEffect(() => {
        LoadFavorites();
    }, []);

    async function LoadFavorites() {
        const cache = new Cache({
            namespace: "Favorites",
            policy: {
                stdTTL: 0
            },
            backend: AsyncStorage
        });
        var text = [];
        const entries = await cache.getAll();
        for (let ent in entries) {
            const val = await cache.get(ent);
            text.push({ name: val[0], symbol: val[1], report: val[2], decision: val[3] });
        }
        setStocks(text);
    }

    return (
        <View>
            <FlatList data={Stocks} renderItem={({ item, separators }) => (
                <TouchableHighlight
                    key={item.name}
                    onPress={() => this._onPress(item)}
                    onShowUnderlay={separators.highlight}
                    onHideUnderlay={separators.unhighlight}>
                    <View style={styles.view}>
                        <Text style={styles.modalText}>{item.name}</Text>
                        <Text style={styles.modalText}>{item.symbol}</Text>
                    </View>
                </TouchableHighlight>
            )}/>
        </View>
    );
}


const styles = StyleSheet.create({
    view: {
        margin: 0,
        flex: 1,
        padding: 10,
        borderColor: 'white',
        borderTopWidth: 2,
        borderBottomWidth: 2,
    },
    text: {
        color: 'white',
        marginTop: 16,
        padding: 4,
        borderWidth: 1,
        borderColor: "#D8D8D8",
        borderRadius: 6,
        backgroundColor: "#291F3F",
        color: "#FFFFFF",
        textAlign: "left",
        fontSize: 12,
        fontWeight: "bold"
    },
    modalText: {
        textAlign: "center",
        color: "white"
    },
    hideModal: {
        textAlign: "center",
        color: "white",
        backgroundColor: "#625D72",
        padding: 4,
        paddingHorizontal: 50,
        borderRadius: 10,
        marginTop: 10,
    },
    modalView: {
        margin: 20,
        backgroundColor: "#3A3441",
        borderRadius: 20,
        padding: 35,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
    },
    button: {
        borderRadius: 20,
        padding: 10,
        elevation: 2
    },
    centeredView: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 22
    },
});
