import * as React from 'react';
import { Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { useEffect, useState } from 'react';
import * as ml5 from "ml5";
import { getCompletion } from "gpt3";
import { Stocks } from "./Stocks";
function HomeScreen() {
  var sL = false;
  var cL = false;
  var sentiment;
  var charRNN;

  const [sentence, setSentence] = useState("Predicting");
  const [report, setReport] = useState("");
  var prediction;
  var res;
  useEffect(() => {
    sentiment = ml5.sentiment('movieReviews', modelReady);
    fetch('http://localhost:3000/api/generate?q=Make%20a%20prediction%20for%20Apple%20stock', { mode: 'cors' }).then((response) => { res = response.text(); console.log(res); res.then((result) => { setReport(result) }) });
  }, [])

  function modelReady() {
    prediction = sentiment.predict("The U.S. Department of Commerce announced that its third estimate for 3Q21 GDP growth was a rate of 2.3%. This was slightly above the previous estimates but in line with the consensus outlook. The growth rate, while positive, was more than 400 basis points below the 1H21 average of 6.5%, due to the emergence of the Delta variant of COVID-19. (The Omicron variant is also likely to have a negative impact on GDP in 2022, but we don't anticipate the impact will be as severe.) The GDP report also includes an inflation gauge, the PCE price index. This index, excluding food and energy, rose at a 4.6% pace, well ahead the Federal Reserve's inflation target of 2.0%, but down from the 6.1% rate recorded in 2Q21. Looking ahead, we expect above-trend GDP growth for the next few quarters. But there may be bumps along the way, including the impact of the Omicron variant on jobs and consumer spending on services, as well as supply-chain bottlenecks. Our forecast for 2022 GDP growth is 3.7%, compared to our estimate for 2021 GDP growth of 5.5%.");
    console.log(prediction);
    if (prediction.score >= .5) {
      setSentence("Buy!");
    }
    else {
      setSentence("Sell!");
    }
  }
  function Predict() {
    charRNN.generate({ seed: 'I like Apple stock because ', length: 200 }, (err, results) => {
      console.log(results);
      setReport('I like Apple stock because ' + results.sample);
    });
  }
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Home!</Text>
      <Text>{sentence}</Text>
      <Text>{report}</Text>
      <Stocks symbols={["AAPL","TSLA","GME"]} names={["Apple"]}/>
    </View>
  )
}

function SettingsScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Settings!</Text>
    </View>
  );
}

const Tab = createBottomTabNavigator();

function MyTabs() {
  return (
    <Tab.Navigator>
      <Tab.Screen name="Home" component={HomeScreen} />
      <Tab.Screen name="Settings" component={SettingsScreen} />
    </Tab.Navigator>
  );
}

export default function App() {
  return (
    <NavigationContainer>
      <MyTabs />
    </NavigationContainer>
  );
}
