import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Image,
  Button,
} from 'react-native';
import { useEffect, useState } from 'react';
import CardStack, { Card } from 'react-native-card-stack-swiper-stek';
import { Symbols, Names } from "./Data";
import { Cache } from "react-native-cache";
import AsyncStorage from '@react-native-async-storage/async-storage';
var _symbols = [];
var _names = [];
function chooseRandomStocks(symbols, names, num) {
  const res = [];
  for (let i = 0; i < num;) {
    const random = Math.floor(Math.random() * symbols.length);
    if (res.indexOf(symbols[random]) !== -1) {
      continue;
    };
    var rn = random;
    _symbols.push(symbols[rn]);
    _names.push(names[rn]);
    i++;
  };
};
chooseRandomStocks(Symbols, Names, 5);

export function Stocks(props) {
  const [Cards, setCards] = useState([]);
  var index = 0;
  useEffect(() => {
    load();
  }, [])
  async function addFavorite(name, symbol, report, decision) {
    const cache = new Cache({
      namespace: "Favorites",
      policy: {
        stdTTL: 0
      },
      backend: AsyncStorage
    });
    var data = [symbol, report, decision];
    await cache.set(name, data);
    console.log("Set ");
  }
  async function load() {
    var tCards = [];
    for (let sym of _symbols) {
      var prediction = await props.makePrediction(_names[index]);
      console.log(prediction);
      tCards.push(
        <Card style={styles.card} key={_symbols[index]} loop={false} >
          <Text style={styles.label} pointerEvents="none">{_names[index]}</Text>
          <Text style={styles.label2} pointerEvents="none">{_symbols[index]}</Text>
          <br></br>
          <Text style={styles.decision} pointerEvents="none">{prediction[0]}</Text>
          <br></br>
          <Text style={styles.report} pointerEvents="none">{prediction[1]}</Text>
          <Button
            onPress={()=>{addFavorite(_names,_symbols,prediction[1],prediction[0])}}
            title="Learn More"
            color="#841584"
            accessibilityLabel="Learn more about this purple button"
          />
        </Card>)
      index++;
      if (index == _symbols.length) {
        console.log("done");
        setCards(tCards);
      }
    }
  }
  return (
    <View style={{ flex: 1 }}>

      <CardStack
        style={styles.content}
        renderNoMoreCards={() => <Text style={{ fontWeight: '700', fontSize: 18, color: 'gray' }}>No more cards :(</Text>}
        onSwiped={() => console.log('onSwiped')}
        onSwipedLeft={() => console.log('onSwipedLeft')}
      >
        {Cards}
      </CardStack>
    </View>
  );
};

/*<Card style={[styles.card, styles.card1]}><Text style={styles.label}>A</Text></Card>
        <Card style={[styles.card, styles.card2]} onSwipedLeft={() => alert('onSwipedLeft')}><Text style={styles.label}>B</Text></Card>
        <Card style={[styles.card, styles.card1]}><Text style={styles.label}>C</Text></Card>
        <Card style={[styles.card, styles.card2]}><Text style={styles.label}>D</Text></Card>
        <Card style={[styles.card, styles.card1]}><Text style={styles.label}>E</Text></Card> */

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#f2f2f2',
  },
  content: {
    flex: 5,
    alignItems: 'center',
    justifyContent: 'center',
  },
  card: {
    width: 300,
    height: 300,
    backgroundColor: '#413563',
    borderRadius: 5,
    shadowColor: 'rgba(0,0,0,0.5)',
    shadowOffset: {
      width: 0,
      height: 1
    },
    padding: 10,
    outlineColor: "#000000",
    outlineStyle: "solid",
    outlineWidth: 4,
    shadowOpacity: 0.5,
  },
  card1: {

    padding: 10,
  },
  card2: {
    backgroundColor: '#FEB12C',
  },
  label: {
    textAlign: 'center',
    fontSize: 20,
    fontFamily: 'System',
    color: '#ffffff',
    backgroundColor: 'transparent',
  },
  label2: {
    textAlign: 'center',
    fontSize: 16,
    fontFamily: 'System',
    color: '#ffffff',
    backgroundColor: 'transparent',
  },
  report: {
    textAlign: 'center',
    fontSize: 11,
    fontFamily: 'System',
    color: '#ffffff',
    backgroundColor: 'transparent',
  },
  decision: {
    textAlign: 'center',
    fontSize: 15,
    fontFamily: 'System',
    color: '#ffffff',
    backgroundColor: 'transparent',
  },
  footer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
  buttonContainer: {
    width: 220,
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  button: {
    shadowColor: 'rgba(0,0,0,0.3)',
    shadowOffset: {
      width: 0,
      height: 1
    },
    shadowOpacity: 0.5,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 0,
  },
  orange: {
    width: 55,
    height: 55,
    borderWidth: 6,
    borderColor: 'rgb(246,190,66)',
    borderRadius: 55,
    marginTop: -15
  },
  green: {
    width: 75,
    height: 75,
    backgroundColor: '#fff',
    borderRadius: 75,
    borderWidth: 6,
    borderColor: '#01df8a',
  },
  red: {
    width: 75,
    height: 75,
    backgroundColor: '#fff',
    borderRadius: 75,
    borderWidth: 6,
    borderColor: '#fd267d',
  }
});
